import React, { useState } from "react";
import { ChevronLeft, ChevronRight } from "lucide-react";
import { useRef,useEffect } from "react";
const logos = [
  "data:image/png;base64,",
  "data:image/png;base64,",
  "data:image/png;base64,",
  "data:image/png;base64,",
  "data:image/png;base64,",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQzopi6uOP07RL0iVSXAbhItqysLGvkKAmVaw&s",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS3JH3ArdI80GfWeHOheANbqMojgZLM18Kb-Q&s",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTOx_GoryDfk9s4qkT_HHtBMrMG54R8xoJXyw&s",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTFdMXCdhGDk1qcbTL0BM9WjogUKyAm3iJ-Gg&s",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQhHZ8XMhAosw6YX8sXmETC2G0qDc4xHMySaA&s",
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR41xo47euIQBib5RdYCCJMUQ5uVmKsT4HkBg&s"

];
const visibleCount = 5;
const AUTO_DELAY = 2500;
const LogoCarousel = () => {
  const [index, setIndex] = useState(0);
  const intervalRef = useRef(null);

  const prev = () => {
    setIndex((prev) => Math.max(prev - 1, 0));
  };

  const next = () => {
    setIndex((prev) =>
      prev < logos.length - visibleCount ? prev + 1 : 0
    );
  };

  useEffect(() => {
    startAutoScroll();
    return stopAutoScroll;
  }, []);

  const startAutoScroll = () => {
    stopAutoScroll();
    intervalRef.current = setInterval(next, AUTO_DELAY);
  };

  const stopAutoScroll = () => {
    if (intervalRef.current) clearInterval(intervalRef.current);
  };

  return (
    <div
      className="relative w-full bg-white"
      onMouseEnter={stopAutoScroll}
      onMouseLeave={startAutoScroll}
    >
      <button
        onClick={prev}
        className="absolute left-2 md:left-6 top-1/2 -translate-y-1/2 bg-white shadow-md rounded-full p-2 z-10 hover:bg-gray-100"
      >
        <ChevronLeft size={24} />
      </button>

      <div className="w-full overflow-hidden px-20">
        <div
          className="flex gap-12 transition-transform duration-500 ease-in-out"
          style={{
            transform: `translateX(-${index * 160}px)`,
          }}
        >
          {logos.map((logo, i) => (
            <img
              key={i}
              src={logo}
              alt="logo"
              className="h-14 w-auto object-contain opacity-80 hover:opacity-100"
            />
          ))}
        </div>
      </div>

      <button
        onClick={next}
        className="absolute right-2 md:right-6 top-1/2 -translate-y-1/2 bg-white shadow-md rounded-full p-2 z-10 hover:bg-gray-100"
      >
        <ChevronRight size={24} />
      </button>
    </div>
  );
};

export default LogoCarousel;